package com.pojo;

public class classes {
					private int cid;
					private String cname;
					public classes(int cid, String cname) {
						super();
						this.cid = cid;
						this.cname = cname;
					}
					public classes() {
						super();
						// TODO Auto-generated constructor stub
					}
					public int getCid() {
						return cid;
					}
					public void setCid(int cid) {
						this.cid = cid;
					}
					public String getCname() {
						return cname;
					}
					public void setCname(String cname) {
						this.cname = cname;
					}
}
